         target        => pTarget,
         context       => 0,
         sofar         => 2,
         totalwork     => 2,
         target_desc   => 'Procesando  ...',
         units         => 'rows');
         
      --BITACORA_CIERRES
      BITACORA_CIERRES_FIN (variables.FEC_HOY);      
      BFinal (vProcName);
   
   EXCEPTION
     WHEN OTHERS THEN 
