                                              Pfecha,
                                              Pfecha,
                                              Pnumasiento,
                                              vCtaPrincipalA,
                                              PAgencia,
                                              PSaldoCC,
                                              'S',
                                              'D',
                                              'N',
                                              PTc1,
                                              PTc2,
                                              variables.Usuario,
                                              vMensajeError
                                              );
                Vsequim    := '1.1';
                              
            if ( vMensajeError is not null ) then              
