                Vsequim    := '1.3';
                
              if ( vMensajeError is not null ) then
                  pMensajeError := vMensajeError;
                    return;
              end if;       
                     
                Vsequim    := '1.4';
        
                -- Intereses por pagar
                Es_Centralizada ( vCtaInteresesA,
                                                    vCentralA);--S o N
                Es_Centralizada ( vCtaInteresesN,
                                                    vCentralN);--S o N                                    
                                                    
