                        pMensajeError := vMensajeError;
                        return;
                    end if;
                    --
                    Vsequim    := '1.7';
          
                    CG.CG_UTL.Lineas_del_Asiento (Pcod_empresa,
                                                    Pcod_Agencia,
                                                    PAplic,
                                                    PSubAplic,
                                                    PTipoTrans,
                                                    PSubTransCambio,
                                                    PNumTransa,
                                                    Pdescripcion,
                                                    Pfecha,
                                                    Pfecha,
                                                    Pfecha,
                                                    Pnumasiento,
                                                    vCtaInteresesN,
                                                    PAgencia,
                                                    PInteresesCC,
                                                    'S',
