                                                                                                ||chr(13)||'Ultimo Proceso Corrido: Elminación'||chr(13)
                                                                                          ||'Código Del Producto: '||chr(13)||'Código Agencia: '
                                                                                          ||chr(13)||'Código cliente: '||c.cod_cliente||chr(13)
                                                                                          ||'Código Sector Contable actual: '||c.COD_SEC_CONTABLE_ACTUAL||chr(13)
                                                                                                ||'Código Sector Contable Nuevo: '||c.COD_SEC_CONTABLE_NUEVO,
                                            pNotifica      =>'N'
                                            );
                      
                      vSubject  := 'Error realizando Cambio de Sector Contable';
                                                        
                    vTexto  := '<style type="text/css">table {border-collapse: collapse;} td, th {border: 1px solid black;padding: 3px;}</style>';
                    vTexto     := vTexto|| '<h3>Error Eliminando</h3>';
