   PROCEDURE CAMBIO_CALENDARIO_PA IS
   
      vProcName           VARCHAR2(80)  := 'CAMBIO_CALENDARIO_PA';
      l_rindex_g          PLS_INTEGER   := DBMS_APPLICATION_INFO.set_session_longops_nohint;
      l_slno_g            PLS_INTEGER;
