                                   Pnumasiento,
                                   VcuentacontableintS,
                                   Pnueva_agencia,                -- tiene Auxiliar  --
                                   PInteresesSu,
                                   'S',                -- Si Acumula         --
                                   'D',                         -- Credito, Tipo movimiento  --
                                   'N',                -- No modifica linea  --
                                   PTc1,              -- Tipo de cambio orig--
                                   PTC2,              -- Tipo de cambio base--
