                                              Pfecha,
                                              Pcliente,
                                              PTitular_agencia,
                                              Pnueva_agencia,
                                              nvl(x.sal_embargado,0) - (nvl(x.mon_int_ret_x_pagar,0) + nvl(x.mon_int_ret_cap,0)),
                                              x.mon_int_ret_x_pagar,
                                              x.mon_int_ret_cap,
