                                  1,
                                  0);
      
        PASE_CUENTAS_CD(V_NEXT_DATE,
                        variables.codempresa,
                        'PA',
                        variables.codidioma,
                        variables.salvar_reg, 
                        trunc(sysdate));
            
        DBMS_APPLICATION_INFO.set_session_longops (
                 rindex        => l_rindex_g,
                 slno          => l_slno_g,
                 op_name       => Job.Ejecuta,
                 target        => pTarget,
                 context       => 0,
                 sofar         => 3,
                 totalwork     => 4,
                 target_desc   => 'Procesando  ...',
                 units         => 'rows');         
            
        actualiza_estatus_proceso(Variables.codempresa,
                                  'PA',
                                  'CUENTAS',
                                  sysdate,
                                  null,
                                  'P',
                                  etiquetas.et_msg_cc_1,
                                  1,
                                  0);
          
        PASE_CUENTAS_CC(V_NEXT_DATE,
                        variables.codempresa,
                        'PA',
                        variables.codidioma,
                        variables.salvar_reg, 
                        trunc(sysdate));
        
        DBMS_APPLICATION_INFO.set_session_longops (
             rindex        => l_rindex_g,
             slno          => l_slno_g,
             op_name       => Job.Ejecuta,
             target        => pTarget,
             context       => 0,
             sofar         => 4,
             totalwork     => 4,
             target_desc   => 'Procesando  ...',
             units         => 'rows');                     
                
        --BITACORA_CIERRES
        BITACORA_CIERRES_FIN (Variables.Fecha);
        BFinal(vProcName); 
                                  
   EXCEPTION
        WHEN OTHERS THEN
            Message (SqlCode||' >> '||SqlErrm); 
            BITACORA_CIERRES_ERROR (Variables.Fecha);
   End EXTRAER_DATOS_PR_CD_PA; 
   
